{
     if(img.__isDirty)
    {
        img.updatePixels();
    }

    if(!img.toImageData && img.loadPixels)
    {
        img.loadPixels();
    }
    
    if(!img.noSourceImg && img.toImageData)
    {
        img.sourceImg = getSourceImg(img);
    }
}

