            fill(35, 210 - 60, 160);
            fastRect(x + unitW2, y, unitW, unitH2);
            fill(35, 210 - 40, 160);
            fastRect(x + unitW, y + unitH2, unitW2, unitH);
            fill(35, 210 - 20, 160);
            fastRect(x, y + unitH, unitW, unitH2);
