            this.img.beginDraw();
                this.img.smooth();
                this.img.noFill();
                this.img.strokeWeight(1);
                this.img.stroke(0, 0, 0);

                for(var i = 0; i < this.size; i += this.step)
                {
                    this.img.rect(i / 2, i / 2, this.width - i, this.height - i, 4);
                }
            this.img.endDraw();

