                            gameObjects.getObject("imageBlock").add(xPos - levelInfo.unitWidth * 0.5, yPos - levelInfo.unitHeight * 0.5, levelInfo.unitWidth * 1.5, levelInfo.unitHeight * 1.5, table['~']).physics.solidObject = false; 
