game.doCursor = function()
{
    game.cursor = false;
    game.noCursor = false;

    // Set player if he/she doesn't exist.
    if(!player || !player.draw)
    {
        player = gameObjects.getObject("player")[0];

        if(player && player.draw)
        {
            // Make sure we're done checking if the player exists or not!
            game.doCursor = function()
            {
                game.cursor = false;
                game.noCursor = false;
            };
        }
    }
};

